// Alternative format: CO2 assessment with file-specific issues
const co2AssessmentHighlights = {
    tool_name: 'assessment',
    data_type: 'co2_analysis',
    data: {
        fileAnalysis: [
            {
                file: 'src/extension.ts',
                issues: [
                    {
                        line: 200,
                        column: 10,
                        endLine: 210,
                        endColumn: 50,
                        severity: 'warning',
                        message: 'This synchronous file operation blocks the main thread. Consider using async alternatives.'
                    },
                    {
                        line: 250,
                        column: 5,
                        severity: 'error',
                        message: 'Memory leak detected: Event listeners not being properly disposed.'
                    }
                ]
            },
            {
                file: 'src/database-highlighter.ts',
                issues: [
                    {
                        line: 100,
                        column: 1,
                        endLine: 120,
                        endColumn: 1,
                        severity: 'info',
                        message: 'Consider implementing caching to reduce database queries and save energy.'
                    }
                ]
            }
        ],
        totalScore: 75,
        recommendations: [
            'Implement connection pooling',
            'Use async file operations',
            'Add caching layer'
        ]
    },
    source: 'carbonara-co2-assessment'
};

async function insertSampleData() {
    // Find the database
    const dbPath = path.join(process.cwd(), 'carbonara.db');
    
    if (!fs.existsSync(dbPath)) {
        console.error(`Database not found at ${dbPath}`);
        console.log('Please ensure carbonara.db exists in the current directory');
        return;
    }

    const db = new sqlite3.Database(dbPath);

    // First, get the project ID from the database
    const projectId = await new Promise<number>((resolve, reject) => {
        db.get('SELECT id FROM projects ORDER BY id DESC LIMIT 1', (err, row: any) => {
            if (err) {
                reject(err);
            } else if (!row) {
                reject(new Error('No project found in database. Please run "carbonara init" first.'));
            } else {
                resolve(row.id);
            }
        });
    });

    console.log(`Using project ID: ${projectId}`);

    // Insert the sample highlights
    const insertPromises = [sampleHighlights, co2AssessmentHighlights].map(highlight => {
        return new Promise<void>((resolve, reject) => {
            const stmt = db.prepare(`
                INSERT INTO assessment_data (project_id, tool_name, data_type, data, source)
                VALUES (?, ?, ?, ?, ?)
            `);

            stmt.run(
                [
                    projectId,
                    highlight.tool_name,
                    highlight.data_type,
                    JSON.stringify(highlight.data),
                    highlight.source
                ],
                function(err) {
                    if (err) {
                        reject(err);
                    } else {
                        console.log(`‚úÖ Inserted ${highlight.tool_name} data with ID: ${this.lastID}`);
                        resolve();
                    }
                }
            );

            stmt.finalize();
        });
    });

    try {
        await Promise.all(insertPromises);
        console.log('\nüéâ Sample data inserted successfully!');
        console.log('Now open VSCode and run the "Refresh Code Highlights" command to see the highlights.');
    } catch (error) {
        console.error('‚ùå Error inserting data:', error);
    } finally {
        db.close();
    }
}

// Run the insertion
insertSampleData().catch(console.error);
